// TodoList.js

import { useState } from "react";

function TodoList() {

    // 상태
    const [todos, setTodos] = useState([{title : "React 복습", isDone : false}, 
                                        {title : "집가서 쉬기", isDone : true}]);

    const [inputValue, setInputValue] = useState('');
    // 함수

    // return() : 화면에 렌더링할 jsx 작성 구문
    return (

        <div>
            <h1>Todo List</h1>
            <input value={inputValue}/>
            <button>추가하기</button>

            <ul>
                {
                    todos.map((todo, index) => (

                        <li>
                            <span style={{ textDecoration : todo.isDone ? 'line-through' : 'none' }}>
                                {todo.title}
                            </span>

                            <button>{todo.isDone ? '미완료' : '완료'}</button>
                            <button>삭제</button>
                        </li>

                    ))
                }
            </ul>
        </div>

    );

}

export default TodoList;
