// TodoList.js

import { useState } from "react";

function TodoList() {

    // 상태
    const [todos, setTodos] = useState([{title : "React 복습", isDone : false}, 
                                        {title : "집가서 쉬기", isDone : true}]);

    const [inputValue, setInputValue] = useState('');
    
    // 함수
    // 할 일 추가 함수
    const handleAddTodo = () => {
        // 기존의 todos 배열과 새로운 할 일 객체를 이어붙여서 새로운 배열을 만든 후,
        // 이를 setTodos 함수를 사용하여 업데이트함.
        setTodos([ ...todos, {title : inputValue, isDone : false}]);
        // 전개 연산자(...) -> 얕은 복사

    };

    // return() : 화면에 렌더링할 jsx 작성 구문
    return (

        <div>
            <h1>Todo List</h1>
            <input value={inputValue} onChange={(e) => setInputValue(e.target.value)}/>
            <button onClick={handleAddTodo}>추가하기</button>

            <ul>
                {
                    todos.map((todo, index) => (

                        <li>
                            <span style={{ textDecoration : todo.isDone ? 'line-through' : 'none' }}>
                                {todo.title}
                            </span>

                            <button>{todo.isDone ? '미완료' : '완료'}</button>
                            <button>삭제</button>
                        </li>

                    ))
                }
            </ul>
        </div>

    );

}

export default TodoList;
